.navbar{background-color:#b7b7b7;width:100%;height:50px;display:flex;display:-webkit-flex;flex-direction:row;-webkit-flex-direction:row;flex-wrap:nowrap;-webkit-flex-wrap:nowrap;justify-content:space-between;-webkit-justify-content:space-between}.navbar .menu-left,.navbar .menu-right{display:block;height:17px;width:50px;padding:18px 20px}._b-overlay,.drawermenu{position:fixed;top:0;height:100%}.drawermenu{background-color:#fff;left:-250px;width:250px;max-width:70%;z-index:100}.drawermenu.drawermenu-right{left:auto;right:-250px}._b-overlay{display:none;cursor:pointer;z-index:80;left:0;right:0;bottom:0;width:100%;background-color:rgba(0,0,0,.5)}.fixed-body{position:fixed;right:0;left:0;overflow:hidden}.ui-page-active{border:0;left:0;position:absolute;top:0;width:100%;z-index:9999}#mypanel,.subcat-layer{position:fixed;z-index:99999}#myrightpanel{position:fixed;height:100%;width:17em;background:#e5e5da;top:0;bottom:0;overflow-y:scroll;-webkit-overflow-scrolling:touch;right:-310px;-webkit-box-shadow:-5px 0 5px rgba(0,0,0,.15);-moz-box-shadow:-5px 0 5px rgba(0,0,0,.15);box-shadow:-5px 0 5px rgba(0,0,0,.15)}#mypanel{width:17em;backface-visibility:hidden;left:-20em;top:0;bottom:-1px;overflow:auto;transform:translate3d(0,0,0)}.subcat-layer{overflow-y:scroll}.subcat-layer.zero-marginleft{margin-left:0}.menuwrapper{width:100%;transition:none}.ui-page-active-pop{border:0;left:0;position:absolute;top:0;z-index:999999;display:block}.ui-popup-hidden{left:0;position:absolute;top:0;visibility:hidden;margin:-1px;width:1px}