    font-size: 1.25rem;line-height: 1.4;
}
.head-group h3{
	font-size:1rem;
}

[data-role="content"]{
  position: relative;
  top:0px;
}
.head-group{position: relative;z-index: 1}
.art-crd-hgt a{font-weight: 600;text-decoration: underline;}
.cntct-list a, .data-height-col a{font-weight: 600;}

/*social sharing widget css*/
.shadow{box-shadow:0 2px 2px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);margin:12px 0px;}
.table{display:table;width:100%;}
.table-cell{display:table-cell;text-align:center;vertical-align:middle;}
.table-cell > *{display:inline-block;vertical-align:middle}
.social-share-widget{background:#fff;}
.social-share-widget-content{padding:8px 0px;font-size: 14px;color: #333;}
.social-share-widget-content .txtmsg{margin-right:5px;}
.border-top .social-share-widget-content{border-top:1px solid #dfdfdf;}
.border-bottom .social-share-widget-content{border-bottom:1px solid #dfdfdf;}
.social-share-widget .at-resp-share-element.at-mobile .at-share-btn{margin-bottom:0px;}
.space-around{margin:0px auto;width:calc(100% - 33px);}
.addthis_inline_share_toolbox{}


/*upcoming exams css*/
    .upcoming-states {border-radius: 2px;margin-bottom: 20px;background: #fff}
    .upcoming-title {font-size: 16px;padding:12px 16px;font-weight: 600;border-bottom: 1px solid #d8d8d8;}
    .upcmng-examlist {padding: 0px 16px;}
    .examslist {padding: 10px 0px;border-bottom: 1px solid #d8d8d8;}
    .anchor_holder a {font-size: 14px;color:#4285f4;font-weight: 600;cursor: pointer;}
    .exams-infodata {font-size: 14px;color: #000;line-height: 1.34;margin: 2px 0px 5px;}
    .exams-infodata strong {font-weight: 600;}
    .upcoming-states .collections_a a.quick-links {font-size: 14px;color:#4285f4;font-weight: 400;}
    .upcoming-states .collections_a b {display: inline-block;color: #ccc;margin: 0 1px;font-weight: 400;}
    .examslist:last-child{border-bottom: none;}
    /*only for mobile case */
    .viewSectn {text-align: center;padding: 10px 0 20px;}
    .viewSectn a{padding: 0 6px;height: 34px;line-height: 32px;border-radius: 2px;}
    /*ends*/
