i.qstns_ico {width: 23px;height: 18px;position: relative;background-position: -430px -45px;top: 4px;}
/*CHP Interlinking start*/
.chp-interlinking{background:#fff;padding:0;box-shadow:0 0 2px 0 rgba(0,0,0,.12),0 2px 2px 0 rgba(0,0,0,.24);border-radius:2px;border:none;position:relative;margin-bottom: 15px;}
.groupcard h2{padding:10px 15px;border-bottom:1px solid #e6e5e5;margin-bottom:0;font-size:1rem;color:#000;font-weight:600}
.chplist{padding:10px 16px;display:flex;flex-flow:row wrap}
.chplinks{width:100%;position:relative;padding-left:15px;margin: 0 0px 5px 0;}
.chplinks:last-child{margin-bottom: 0px;}
.chplinks:before{content:'';width:5px;height:5px;background:#666;display:block;position:absolute;left:0;top:6px;border-radius:50%}
.chplinks a{display:block;color:#008489;font-size:14px;font-weight:600;cursor: pointer;line-height: 1.32;}
.showchplinks{display:block;text-align:center;font-size:14px;color:#008489;font-weight:600;padding:5px 0 15px;position: relative}
.groupcard input {display: none; }
.showchplinks:after{content:"";display:inline-block;width:6px;height:6px;border-bottom:2px solid #008489;border-right:2px solid #008489;margin:0 0 0 9px;transform:rotate(45deg);position: relative;top: -3px;}
.groupcard input[type='radio']:checked~.showchplinks{display:none;}
.chplinks.hidechp{display: none;}
.groupcard input[type='radio']:checked + .chplist > .chplinks.hidechp{display: block}
/*CHP Interlinking end*/
.getFeedtl {display: flex;flex-direction: row;}
.getFeedtl section {margin-left: auto;}
.getFeedtl section a{padding:0 9px;}
.oafcta-btn{background: #fac528;border:1px solid #fac528;color: #000; text-align:center;vertical-align:middle;font-size:14px;font-weight:600;cursor:pointer;height:32px;touch-action:manipulation;white-space:nowrap;background-image:none;line-height:30px;padding:0 16px;display:inline-flex;border-radius:2px;align-items: center;    margin: 7px 0 3px 0;}
.oafcta-btn .d-arrow{    position: relative;
    width: 12px;
    height: 14px;
    margin: 0px 5px 0px 0px;}
.oafcta-btn .d-arrow:after,.oafcta-btn .d-arrow:before {
    content: "";
    display: inline-block;
    width: 0px;
    height: 0px;
    border-left: 7px solid #000;
    border-top: 7px solid transparent;
    border-right: 7px solid transparent;
    border-bottom: 7px solid transparent;
    border-radius: 3px;
    position: absolute;
    left: 0px;
}
.oafcta-btn .d-arrow:after{
  left: 5px;
}
.dot-div {
    border: 1px dashed #e6e5e5;
    text-align: center;
}