/*CHP Interlinking start*/
.chp-interlinking{background:#fff;padding:0;box-shadow:0 0 2px 0 rgba(0,0,0,.12),0 2px 2px 0 rgba(0,0,0,.24);border-radius:2px;border:none;position:relative;margin-bottom: 15px;}
.groupcard h2{padding:10px 15px;border-bottom:1px solid #e6e5e5;margin-bottom:0;font-size:16px;color:#000;font-weight:600}
.chplist{padding:10px 16px;display:flex;flex-flow:row wrap}
.chplinks{width:100%;position:relative;padding-left:15px;margin: 0 0px 5px 0;}
.chplinks:last-child{margin-bottom: 0px;}
.chplinks:before{content:'';width:5px;height:5px;background:#666;display:block;position:absolute;left:0;top:5px;border-radius:50%}
.chplinks a{color:#008489;font-size:14px;font-weight:600;cursor: pointer;display: block;line-height: 1.32}
.showchplinks{display:block;text-align:center;font-size:14px;color:#008489;font-weight:600;padding:5px 0 15px;position: relative}
.groupcard input {display: none; }
.showchplinks:after{content:"";display:inline-block;width:6px;height:6px;border-bottom:2px solid #008489;border-right:2px solid #008489;margin:0 0 0 9px;transform:rotate(45deg);position: relative;top: -3px;}
.groupcard input[type='radio']:checked~.showchplinks{display:none;}
.chplinks.hidechp{display: none;}
.groupcard input[type='radio']:checked + .chplist > .chplinks.hidechp{display: block}
/*CHP Interlinking end*/
/**l2 menu css**/
.clg-nav{margin-bottom:10px;padding-top:10px;background:#fff;border-left:1px solid #dfe0e4;border-right:1px solid #dfe0e4;border-bottom:1px solid #e6e5e5}
.clg-nav .clg-navList{padding:0 0 0 5px;margin-bottom:0}
.clg-nav .clg-navList ul{width:100%;display:block}
.clg-nav .clg-navList ul>li{list-style:none;display:inline-block;font-size:.875rem;text-align:center}
.clg-nav .clg-navList ul>li a.active{border:1px solid #008489;color:#fff;font-weight:600;background:#008489}
.clg-nav .clg-navList ul>li a{padding:0 8px;color:#008489;display:inline-block;border:1px solid #008489;font-size:1rem;border-radius:2px;background:#fff;margin:0 7px 10px 0;line-height:1.58}
.articles_box{display: block;}
.article_block {display: flex;padding-bottom: 10px;border-bottom: 1px solid #dfdfdf;margin-bottom: 10px;}
.article_block:last-child{margin: 0px;border:none;padding: 0px;}
.pwa_thumbnail{width: 97px;height: 73px;margin-right: 12px;display: inline-block;  background-color: #f9f9f9;overflow: hidden;}
.pwa_thumbnail img {width: 100%;}
.article_infocol{display: inline-block;width: 70%;vertical-align: top;}
.article_infocol a{font-size: 1rem;line-height: 1.32;font-weight: 600;text-decoration: none;}
.article_infocol p{font-size: 0.875rem;color: #666;}
.article-wrap{text-align:center;}
.article-wrap .trnBtn{border: 1px solid #008489;color: #008489;font-size: 0.875rem;font-weight: 600;padding: 0px 10px;text-align: center;cursor: pointer;background: #fff;text-decoration: none;display: inline-block;line-height: 34px;-webkit-tap-highlight-color: transparent;height: 36px;border-radius: 2px;position: relative;margin-top: 15px;}